settings/global/typography/typography.sc:
// =============================================================================
// TYPOGRAPHY
// =============================================================================


// Global Type Vars
// -----------------------------------------------------------------------------
//
// IMPORTANT: These variables are NOT to be consumed in components/apps.
//            Instead use the maps and functions outlined below.
//
// 1. Set up the REM units for the rest of the framework (pixel value).
// The stencilNumber() function outputs a pixel value by default.
// 2. Your base font size should always start at 1rem;
// 3. Your <body> line-height. This value is used for spacing calculations,
//    where spacing is = $fontSize-root * $lineHeight-base.
//
// -----------------------------------------------------------------------------


// Font families
// -----------------------------------------------------------------------------

$fontFamily-sans:               $theme-body-font-family, Arial, Helvetica, sans-serif; // papathemes-beautify
$fontFamily-serif:              null;
$fontFamily-mono:               null;
$fontFamily-headings:           $theme-header-font-family, Arial, Helvetica, sans-serif; // papathemes-beautify

$fontFamily-hero:               $theme-header-font-family, Arial, Helvetica, sans-serif; // papathemes-beautify


// Font sizes
// -----------------------------------------------------------------------------

$fontSize-root:                 stencilNumber("fontSize-root"); // 1
$fontSize-base:                 1rem; // 2
$fontSize-hero:                 64px; // papathemes-beautify
$fontSize-largest:              stencilNumber("fontSize-h1");
$fontSize-larger:               stencilNumber("fontSize-h2");
$fontSize-large:                stencilNumber("fontSize-h3");
$fontSize-small:                stencilNumber("fontSize-h4");
$fontSize-smaller:              stencilNumber("fontSize-h5");
$fontSize-smallest:             stencilNumber("fontSize-h6");
$fontSize-tiny:                 12px;
$fontSize-logo-large:           stencilNumber("logo_fontSize");
$fontSize-logo-medium:          $fontSize-logo-large - 6px;
$fontSize-logo-small:           $fontSize-logo-medium - 2px;


// Line heights
// -----------------------------------------------------------------------------

$lineHeight-base:               1.5; // 3
$lineHeight-hero:               50px;
$lineHeight-largest:            37px;
$lineHeight-larger:             32px;
$lineHeight-large:              29px;
$lineHeight-small:              24px;
$lineHeight-smaller:            19px;
$lineHeight-smallest:           15px;
$lineHeight-tiny:               18px;


// Font weights
// -----------------------------------------------------------------------------

$fontWeight-thin:               300;
$fontWeight-normal:             400;
$fontWeight-medium:             500;
$fontWeight-semibold:           600;
$fontWeight-bold:               700;
$fontWeight-black:              900;


// Letter spacing
// -----------------------------------------------------------------------------

// papathemes: mods letter spacing
$letterSpacing-base:            0;
$letterSpacing-largest:         1.5px;
$letterSpacing-larger:          1px;
$letterSpacing-large:           0.5px;
$letterSpacing-small:           -0.5px;
$letterSpacing-smaller:         -1px;
$letterSpacing-smallest:        -1.5px;

fonts/papathemesicons.scss:
@font-face {
  font-family: 'papathemesicons';
  src:  url('#{$icomoon-font-path}/papathemesicons.eot?jpwbml');
  src:  url('#{$icomoon-font-path}/papathemesicons.eot?jpwbml#iefix') format('embedded-opentype'),
    url('#{$icomoon-font-path}/papathemesicons.woff2?jpwbml') format('woff2'),
    url('#{$icomoon-font-path}/papathemesicons.ttf?jpwbml') format('truetype'),
    url('#{$icomoon-font-path}/papathemesicons.woff?jpwbml') format('woff'),
    url('#{$icomoon-font-path}/papathemesicons.svg?jpwbml#papathemesicons') format('svg');
  font-weight: normal;
  font-style: normal;
  font-display: swap;
}

[class^="papathemes-icon-"], [class*=" papathemes-icon-"] {
  /* use !important to prevent issues with browser extensions that change fonts */
  font-family: 'papathemesicons' !important;
  speak: none;
  font-style: normal;
  font-weight: normal;
  font-variant: normal;
  text-transform: none;
  line-height: 1;

  /* Better Font Rendering =========== */
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

