components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu {{#if menu_type '==' 'vertical'}}navPages-action--vertical{{/if}}" href="{{url}}"
    {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
    {{#if id '==' category.id}}data-current-category{{/if}}
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#if menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-add" /></svg></i>
</a>
<div class="navPage-subMenu
        {{#if menu_type '==' 'vertical'}}
            navPage-subMenu--vertical
        {{else if menu_type '==' 'standard'}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-add" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
                        </li>
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/common/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings
                                    menu_type=../../../menu_type}}
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}" data-instantload {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="_accordion-block">
    <div
        class="_accordion-navigation toggleLink {{#unless ../start_collapsed }} is-open {{/unless}}"
        role="button"
        tabindex="0"
        data-collapsible="#facetedSearch-content--{{dashcase facet }}">
        <h4 class="_accordion-title">
            {{ title }}
        </h4>

        <div class="_accordion-navigation-actions">
            {{#if selected}}
                <a href="{{ remove_url }}" class="facetedSearch-clearLink" data-faceted-search-facet>{{lang 'search.faceted.clear'}}</a>
            {{/if}}
            <svg class="icon _accordion-indicator toggleLink-text toggleLink-text--off">
                <use xlink:href="#icon-keyboard-arrow-down" />
            </svg>
            <svg class="icon _accordion-indicator toggleLink-text toggleLink-text--on">
                <use xlink:href="#icon-keyboard-arrow-up" />
            </svg>
        </div>
    </div>

    <div id="facetedSearch-content--{{dashcase facet }}" class="_accordion-content {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet }}" class="navList" data-facet="{{facet}}" data-has-more-results="{{ has_more_results }}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{ url }}"
                        class="navList-action navList-action--checkbox {{#if selected }} is-selected {{/if}} {{#if has_sub_categories }} has-children {{/if}}"
                        rel="nofollow"
                        data-id="{{ id }}"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            {{#if count}}
                                <span>({{ count }})</span>
                            {{/if}}
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

